    <canvas id="myChart" style="width: 800px; height: 460px; margin-top: -12px;"></canvas>
    <script>
        
        let dataX = []
        let dataY = []
        const data_sheet = {
            labels: dataY,                                  // Y Values 
                datasets: [{
                data: dataX,                                // X Values
                backgroundColor: [] ,
                borderColor: [],
                fill: true,                                 // fill background-color
                }]
            }

        const config = {
            type: 'line',
            data: data_sheet,
            options: {
            title: {
            display:true,
            text: "Temperature"
        },
        legend:{
            display:false
        },
        scales: {
            yAxes: [{
                ticks: {
                    min: 5,
                    max: 45
                }
            }]
        }
    }
}
    const ctx = document.getElementById('myChart').getContext('2d');
    const myChart = new Chart(ctx, config);

    function getBackgroundColor(temperature) {
    if (temperature <=30 ) {
        return 'rgba(1, 1, 122, 0.2)';
    } else if (temperature >= 31 && temperature <= 39) {
        return 'rgba(0, 255, 0, 0.1)';
    } else if (temperature == 40 ) {
        return 'rgb(253, 194, 32, 0.2)';
    } else {
        return 'rgba(255, 0, 0, 0.1)';
    }
}

        function updateChartData(){
        var xhttp = new XMLHttpRequest();
        xhttp.onreadystatechange = function() {
            if (this.readyState == 4 && this.status == 200) {
                var response = JSON.parse(this.responseText);
                dataX = response.dataX;
                dataY = response.dataY;
                
                myChart.data.labels = dataX;
                myChart.data.datasets[0].data = dataY;
                myChart.data.datasets[0].backgroundColor = dataY.map(getBackgroundColor);
                myChart.data.datasets[0].borderColor = dataY.map(getBackgroundColor);
                myChart.update();
            }
        };
        xhttp.open("GET", "tableView.php", true);
        xhttp.send();
    }

    setInterval(updateChartData, 500);

</script>

